import React, { useState, useEffect } from 'react'; // Import useEffect
import { sendMessage } from '../utils/mockDatabase'; // Update import
import { useCookieConsent } from '../utils/cookies';
import Mail from '../utils/Mail';
import { db } from '../firebase';

const imageContext = require.context('../utils/database-img', true, /\.(jpg)$/);

const Home = ({ userId }) => {
  useCookieConsent();

  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const userDoc = await db.collection("users").doc(userId).get();
      setUser(userDoc.data());
    };

    fetchData();
  }, [userId]);

  const [activeTab, setActiveTab] = useState('contact');
  const [newMessage, setNewMessage] = useState('');
  const [receiverId, setReceiverId] = useState('');

  const handleSendMessage = async () => { // Made this an async function
    const messageObj = {
      fromName: user.username,
      fromEmail: user.email,
      message: newMessage,
      timestamp: new Date().toLocaleString(),
    };

    if (await sendMessage(userId, receiverId, messageObj)) { // Await sendMessage
      setNewMessage('');
      setReceiverId('');
    } else {
      console.error("Failed to send the message. User IDs might be incorrect.");
    }
  };

  // Get Time
  const getUTCTime = () => {
    const now = new Date();
    const utcTime = now.toUTCString();
    return utcTime;
  };

  return (
    <div id="homePage">
      <div className="homepage-container">
        {/* ...existing code */}
        {activeTab === "third" && (
          <div className="mailbox">
            <ul>
              {user.mailbox.map((email, index) => (
                <li key={index}>
                  <div>Name: {email.fromName}</div>
                  <div>Email: {email.fromEmail}</div>
                  <div>Message: {email.message}</div>
                  <div>Timestamp: {email.timestamp?.toDate().toLocaleString()}</div> {/* Convert to date-time string */}
                </li>
              ))}
            </ul>
          </div>
        )}
        {/* ...existing code */}
      </div>
    </div>
  );
};

export default Home;
