import React, { useEffect, useState } from 'react';
import { db } from '../firebase';
import { collection, getDocs } from 'firebase/firestore';

const Mail = () => {
  const [contactForms, setContactForms] = useState([]);
  const [joinForms, setJoinForms] = useState([]);
  const [activeTab, setActiveTab] = useState('contact'); // Initialize activeTab state

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch all documents from contactForms collection
        const contactSnapshot = await getDocs(collection(db, 'contactForms'));
        const contactData = contactSnapshot.docs.map((doc) => ({id: doc.id, ...doc.data()}));
        setContactForms(contactData);

        // Fetch all documents from joinForms collection
        const joinSnapshot = await getDocs(collection(db, 'joinForms'));
        const joinData = joinSnapshot.docs.map((doc) => ({id: doc.id, ...doc.data()}));
        setJoinForms(joinData);
      } catch (error) {
        console.log('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <div className="tabs">
        <div className={`tab ${activeTab === 'contact' ? 'active' : ''}`} onClick={() => setActiveTab('contact')}>
          Contact Forms
        </div>
        <div className={`tab ${activeTab === 'join' ? 'active' : ''}`} onClick={() => setActiveTab('join')}>
          Join Forms
        </div>
      </div>

      {activeTab === 'contact' && (
        <div className="mailbox">
          <h2>Contact Forms</h2>
          <ul>
            {contactForms.map((form, index) => (
              <li key={index}>
                ID: {form.id} <br />
                Name: {form.name} <br />
                Email: {form.email} <br />
                Message: {form.message}
              </li>
            ))}
          </ul>
        </div>
      )}

      {activeTab === 'join' && (
        <div className="mailbox">
          <h2>Join Forms</h2>
          <ul>
            {joinForms.map((form, index) => (
              <li key={index}>
                ID: {form.id} <br />
                Name: {form.name} <br />
                Email: {form.email}
              </li>
            ))}
          </ul>
        </div>
      )}

    </div>
  );
};

export default Mail;
