import React, { useState } from 'react';
import { usersDatabase, sendMessage } from '../utils/mockDatabase';
import { useCookieConsent } from '../utils/cookies';
import Mail from '../utils/Mail';


const imageContext = require.context('../utils/database-img', true, /\.(jpg)$/);
const Home = ({ userId }) => {
  useCookieConsent();
  const [activeTab, setActiveTab] = useState('contact'); // Initialize activeTab state

  const [newMessage, setNewMessage] = useState('');
  const [receiverId, setReceiverId] = useState('');

  const handleSendMessage = () => {
    const messageObj = {
      fromName: user.username,
      fromEmail: user.email,
      message: newMessage,
      timestamp: getUTCTime(),
    };

    sendMessage(userId, receiverId, messageObj);
  };

  // Get Time
  const getUTCTime = () => {
    const now = new Date();
    const utcTime = now.toUTCString();
    return utcTime;
  };
  // Find the user with the given ID in the mock database
  const user = usersDatabase.find((user) => user.id === userId);
  // Dynamic image import using require
  const userProfileImage = imageContext(`./${user.image}`);

  return (
    <div id="homePage">
      <div className="homepage-container">
        <div className="left-section">
          <div className="user-info">
            {/* Fetch image from the mock database */}
            <img src={userProfileImage} alt="User" />
            <div className="username">{user.username}</div>
            <div className="email">{user.email}</div>
          </div>
          <div className="login-time">
            Logged in at: <span>{getUTCTime()}</span>
          </div>
        </div>

        <div className="right-section">
          <div className="tabs">
            <div className={`tab ${activeTab === 'contact' ? 'active' : ''}`} onClick={() => setActiveTab('contact')}>
              Contact Forms
            </div>
            <div className={`tab ${activeTab === 'join' ? 'active' : ''}`} onClick={() => setActiveTab('join')}>
              Join Forms
            </div>
            <div className={`tab ${activeTab === 'third' ? 'active' : ''}`} onClick={() => setActiveTab('third')}>
              User Mailbox
            </div>
          </div>
          <Mail activeTab={activeTab} setActiveTab={setActiveTab} />
          {activeTab === "third" && (
            <div className="mailbox">
              <ul>
                {user.mailbox.map((email, index) => (
                  <li key={index}>
                    <div>Name: {email.fromName}</div>
                    <div>Email: {email.fromEmail}</div>
                    <div>Message: {email.message}</div>
                    <div>Timestamp: {email.timestamp}</div>
                  </li>
                ))}
              </ul>
              {/* New message sending form */}
          <div className="send-message-form">
            <input type="text" placeholder="Receiver ID" value={receiverId} onChange={(e) => setReceiverId(e.target.value)} />
            <input type="text" placeholder="New message" value={newMessage} onChange={(e) => setNewMessage(e.target.value)} />
            <button onClick={handleSendMessage}>Send</button>
          </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Home;
