import { useRef } from "react";
import { addDoc, collection, getD } from "firebase/firestore";
import { db } from "../firebase";

export const sendMessage = async (fromId, toId, messageObj) => {
  const userRef = db.collection("users");

  try {
    const fromUser = await getUser(fromId);
    const toUser = await getUser(toId);

    if (!fromUser || !toUser) {
      return false;
    }

    messageObj.timestamp = getTimestamp();

    await updateUser(fromId, {
      sentMessages: addMessage(fromUser.sentMessages, messageObj),
    });

    await updateUser(toId, {
      mailbox: addMessage(toUser.mailbox, messageObj),
    });

    return true;
  } catch (error) {
    console.error("Error sending message: ", error);
    return false;
  }
};

const getUser = async (userId) => {
  const userDoc = await db.collection("users").doc(userId).get();
  return userDoc.exists ? userDoc.data() : null;
};

const updateUser = async (userId, data) => {
  await db.collection("users").doc(userId).update(data);
};

const addMessage = (messageList, messageObj) => {
  return [...messageList, messageObj];
};

const getTimestamp = () => {
  return firebase.firestore.FieldValue.serverTimestamp();
};
